INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '血液透析', 1, 0.0, '使用血液透析机和相应管路，将患者血液引出体外并利用透析器进行普通透析治疗。连接透析管路并预充，对动静脉通路或透析导管严格消毒，穿刺动静脉通路或连接透析导管，必要时超声引导穿刺。按透析处方设定透析机参数，透析过程监测生命体征。完成透析治疗后断开管路，压迫止血(动静脉通路)或封管(透析导管)。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '永久起搏器更换术', 1, 0.0, '清洁皮肤，安放贴片电极，监护仪监护下，消毒铺巾，局部麻醉，必要时行临时起搏保证安全，监护仪监护下切开囊袋取出起搏器，测试原起搏电极导线阈值、感知、阻抗、振幅等参数，符合要求则直接与更换的永久起搏器连接，置入皮下囊袋，庆大霉素冲洗囊袋，逐层缝合皮下组织及皮肤。起搏电极导线置入与永久起搏器安置术时相同，测定参数，寻找合适的起搏部位，固定新起搏电极导线，与永久起搏器连接。', '', '', '', 1, 2);
