INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '结膜囊成形术', 1, 0.0, '局部麻醉，儿童或不合作者全身麻醉，消毒铺巾，开睑，切开结膜，结膜下分离至近眶缘。分离瘢痕组织，根据结膜缺损范围取上臂内侧适当大小的全厚皮瓣，供区直接拉拢缝合。修整所取皮片，剪除皮下脂肪，将皮片植入眼窝，将皮片边缘与睑缘或残留结膜缝合。眼窝内置入透明眼模，并将植片平铺。行睑缘永久性粘连缝合。结膜内涂抗生素眼膏，加压包扎。如结膜囊缺损范围小，可选取羊膜、异体组织或生物材料移植。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '眼眶减压术', 1, 0.0, '指对甲状腺相关眼病及其他眼病等导致的眼球突出、高眶压、视神经病变等的手术治疗。麻醉，消毒铺巾，可通过前路开眶，外侧开眶，打开眶内壁、下壁或外壁，去除骨壁范围充分，剥离骨膜，打开相应位置肌间隔，向相应鼻窦或颞窝释放眶内脂肪，降低眶压，逐层缝合，避免损伤硬脑膜及眼外肌。不含眶隔修补术、眼眶内充填、眼窝再造、眼睑结膜成形。', '', '', '', 1, 2);
