INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '开胸止血术', 1, 0.0, '麻醉，消毒铺巾，取胸后外侧或前外侧切口，切开皮肤、电刀逐层切开皮下组织、肌肉及胸膜进入胸腔。进胸后清除胸腔内积存的血凝块及积血，记量，接着迅速探查出血部位。如为胸廓内血管或肋间血管出血，可用血管钳钳夹，贯穿缝合结扎即可止血。如为肺裂伤出血，常有血气胸，应找到出血漏气的部位，予以缝合结扎。如肺组织损伤严重，损伤血管也较大，一般缝合不能很好止血，可行肺叶切除。如为心脏或大血管损伤，则应做相应处理。', '', '', '', 1, 2);
