INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '混合痔外剥内扎术', 1, 0.0, '手术室中，手术野消毒铺巾，麻醉后，指诊及肛门镜探查肛管直肠，扩肛，剥离外痔至齿线，钳夹内痔基底部，结扎或缝扎内痔，距结扎线0.5厘米剪断痔组织。术中充分止血，检查无渗出血，皮桥完整，创面引流通畅后，肛管内放置油纱条，外敷止血材料和纱布，胶布固定。', '', '', '', 1, 2);
