INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '肛瘘封堵术', 1, 0.0, '手术室中，完成麻醉后，手术野消毒铺巾。消毒肛管直肠，肛门直肠指诊，经肛门镜探查肛管直肠，确定原发感染部位后，用刮匙经肛瘘外口进入，搔刮瘘管管道中坏死组织，仔细冲洗创面，充分止血，将充填材料填入瘘管，检查创面无渗、出血，引流通畅，外敷塔形纱布，胶布固定。', '', '', '', 1, 2);
