INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('检查费', 'A007', '超声支气管镜检查', 2, 0.2, '咽部麻醉、润滑，插入超声支气管镜，观察气道变化，将超声水囊充满，观察支气管壁及壁外结构，定位后进行经支气管淋巴结针吸活检。标本送细胞学和组织学等检查。不含病理学检查、活检。', '', '', '', 1, 2);
