INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('化验费', 'A008', '分段采血比较全身不同部位激素水平', 2, 0.2, '试验在介入室进行，试验前知情同意。试验当日患者空腹，取仰卧位，局部麻醉后，采用静脉血管造影技术，利用血管穿刺针进行股静脉穿刺，将血管鞘置入右侧股静脉中，随后将导管置入血管鞘中。通过导引钢丝引导导管自股静脉到达预定位置后，推注1-2毫升非离子性的对比剂确定到位，经过导管抽取血样测定激素水平，抽取血样前需丢弃留存在导管中的4毫升血液。其它部位重复。医生分析测定结果。不含实验室检验。不含监护和DSA引导', '', '', '', 1, 2);
