INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '宫腔观察吸引术', 2, 0.2, '术前行宫颈预处理，消毒铺巾，放置窥器，暴露宫颈，消毒阴道及宫颈，探测宫腔深度，扩宮器依次扩张宮颈后，置入合适型号的一次性摄像吸引管。通过一次性摄像吸引管前端摄像头直视定位观察宫颈、宫颈管和宫腔内组织(宫腔内膜、内膜息肉、孕囊组织等)，采集图片或视频。再用一次性摄像吸引管对宫腔组织行定点刮取或吸除。术后探宫腔，观察出血，并检查吸出物的完整性或送检，详细记录手术病例。不含病理学检查。', '', '', '', 1, 2);
