INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '无回吸口腔治疗术', 3, 1.0, '指需要牙体预备、去腐、窝洞制备治疗以及口腔颌面外科局部手术的患者。评估患者情况，核对医嘱及患者信息，消毒铺巾，局醉或神经阻滞麻醉，切开相关病变组织，使用一次性高速涡轮牙钻手机(零回吸)在患者与患者之间做物理隔离，在关键手术器具传递与复杂且庞大的“清洗消毒保养程序”之间做行为隔离，彻底阻断细菌和病毒的通路，配合微创技术手法完成切割、打磨、钻孔、扩孔、劈冠等操作，反复冲洗创面，清创缝合。患者恢复舒适体', '', '', '', 1, 2);
