INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('检查费', 'A007', '胎儿系统彩色多普勒超声检查', 3, 1.0, '系统性检查胎儿颅腔、脊柱、口鼻、心脏四腔心、胸部、腹部、膀胱、四肢长骨等，并进行胎儿双顶径、头围、腹围、股骨、羊水量测量，观察脐带血流、胎盘。进行标准切面图像留存。作出诊断，图文报告。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '亚低温治疗', 3, 1.0, '评估病情；洗手、戴手套；准备亚低温治疗仪，设定初始温度，每15分钟测量记录肛温一次，达到目标温度时每一个小时测肛温，维持治疗期间每2小时记录肛温一次，期间每两小时翻身、检查皮肤、记录呼吸、心率、血压。72小时后复温，每小时记录肛温一次。使用完毕消毒主机及毯面。', '', '', '', 1, 2);
