INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '肾脏低温机械灌注', 3, 1.0, '对肾脏进行修剪，清除多余脂肪等组织，将动脉瓣膜暴露出来，并用保存液将血管内的代谢产物冲洗干净。铺无菌工作台，储冰盒装冰，调整冰盒温度。将肾脏灌注液注入储肾盒内，连接输注循环管路套装和肾脏灌注运转箱，启动运转箱使器官保存液充满输注管路并循环，人工调整肾脏灌注运转箱并排空管路内气体。检查并修剪供肾，修剪过程中实时观察肾脏是否有漏液情况，如有漏液应及时将漏液点进行结扎，确保肾脏无漏液，留出合适瓣膜与套管', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('检查费', 'A007', '叶酸受体介导宫颈染色检查', 3, 1.0, '取膀胱截石位，铺巾，暴露宫颈阴道，清理分泌物，取宫颈、宫颈管及阴道细胞组织，进行叶酸受体介导荧光染色检查，出具报告。按规定处理废弃物；接受临床相关咨询。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('诊疗费', 'A006', '经口电子胆管镜检查', 3, 1.0, '经口电子胰管镜按此标准进行收费。内涵:咽部麻醉，镇静，润滑，消饱，电子十二指肠镜经口插至乳头部位，胰胆管造影，将胆管镜自母镜活检通道插入，经乳头开口沿导管插入胆管内，通过胆管镜进行检查。图文报告。不含监护、x线检查、活检、病理学检查。', '', '', '', 1, 2);
