INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '冠状动脉内血栓抽吸术', 3, 1.0, '以1支血管为基价，每增加1支加收不超过20%内涵:在备有除颤仪及除颤电极的条件下，消毒铺巾，局部麻醉，穿刺动脉，放置鞘管，冠状动脉造影后经鞘管在监护仪监护及血管造影机X线引导下，沿引导钢丝将指引导管送至冠状动脉开口，根据冠状动脉造影结果决定需要进行血栓抽吸的血管，沿指引钢丝将抽吸导管送至病变近端，抽吸导管近端连接负压抽吸装置。打开抽吸装置后谨慎前送导管，之后回撤。不含监护、DSA引导。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '经尿道前列腺剜除术', 3, 1.0, '取膀胱截石位，消毒铺巾，置剜除镜进入膀胱，确定双侧输尿管开口及精阜位置，行前列腺剜除。', '', '', '', 1, 2);
