INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '电热针灸治疗', 1, 0.0, '使用电子冷热针灸仪器，电流通过专用的针具产生合适的热量，针尖部的温度可在40-700℃之间，局部75%酒精消毒，针刺入穴位、得气，通电产生热量。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '眼针治疗', 1, 0.0, '按照眼针疗法的取穴原则和方法，在眼周选择特定穴区，选取适合规格的毫针，采用直刺或平刺法实施操作。出针时要注意压迫止血。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '耳针治疗', 1, 0.0, '按照耳针疗法的取穴原则和方法，在耳部选择特定穴区，将毫针快速刺入皮下，采用捻转手法实施操作，治疗过程中，必须严格消毒，以防耳部感染。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '普通电针治疗', 1, 0.0, '在毫针针刺基础上，将电针仪的输出电极连接在毫针针柄上，给予电刺激进行治疗。根据病情，选择适宜的电脉冲波型、变换频率、刺激强度、治疗时间。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '芒针治疗', 1, 0.0, '选用100毫米以上的专用长针，采用特别的进针方法进针，当针刺达到一定深度后实行捻转手法，按一定的规律结合轻重、快慢、方向的不同要求完成补泻手法。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '滑囊炎推拿治疗', 1, 0.0, '患者坐位，术者用㨰法、按法、揉法、拿法在患部操作，然后用关节摇法、抖法舒筋通络，滑利关节。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '静脉放血治疗', 1, 0.0, '根据病情选定体表某部位较大的静脉，采取相应的措施使静脉充血鼓胀，持放血针具快速刺入，快速出针，适量放血，结束后压迫止血。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '腕踝针治疗', 1, 0.0, '按照腕踝针疗法的取穴原则和方法，在腕踝部选择特定穴区，选取适合规格的毫针，采用平刺法实施操作。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '隔物灸治疗', 1, 0.0, '根据病情、患者体质和穴位等，确定使用的间隔药物并对药物进行中药饮片调配临方复杂炮制，安置体位，选择合适的灸量，密切观察灸处肤色变化和患者神情反应，以了解灸处感觉和病情变化，及时调整灸量，防止烫伤。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '火针治疗', 1, 0.0, '根据病情，选用不同规格的火针，将火针由针身向针尖逐渐烧红至发白，对准穴位迅速刺入到一定深度，操作方法有点刺、散刺、浅刺、深刺等，进针、出针均要求快速、准确，应特别注意避开血管、肌腱、神经干及内脏器官，面部慎用。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '口针治疗', 1, 0.0, '按照口针疗法的取穴原则和方法，在口腔黏膜上的特定穴区，选取适合规格的毫针，采用斜刺、捻转手法实施操作。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '耳穴压丸治疗', 1, 0.0, '一手固定耳廓，另一手用镊子夹取压丸贴压耳穴，并适度按揉，出现微痛、胀、热等感觉，根据病情嘱患者定时按揉。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '梅花针治疗', 1, 0.0, '采用梅花针进行穴位和患部局部叩刺，以腕部弹力均匀有节奏叩打皮肤，刺激强度分轻、中、重三种。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '鼻针治疗', 1, 0.0, '按照鼻针疗法的取穴原则和方法，在鼻部选择特定穴区，选取适合规格的毫针，采用平刺、斜刺或直刺针法实施操作。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '普通拔罐治疗', 1, 0.0, '根据病情选定闪罐和/或留罐方法，选用经过消毒的普通罐具在治疗部位皮肤上闪拔和/或留置。用闪罐法治疗，用闪火法将罐吸拔于应拔部位，随即取下、再吸拔、再取下，反复吸拔至局部皮肤潮红为度，用留罐法治疗，要将吸拔于皮肤上的罐具留置一定时间，使局部皮肤潮红或皮下瘀血后再将罐具取下。操作过程中，要密切关注拔罐部位皮肤的变化，避免不必要的起泡现象。用留罐法治疗，如果治疗需要起泡，要先征求患者同意。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '穴位埋线治疗', 1, 0.0, '皮肤常规消毒(消毒后或可局部麻醉)，若采用套管针埋线法或埋线针埋线法治疗，取适当长度的可吸收性外科缝线，放入针的前端，刺入穴位，达到所需深度，施以适当的提插捻转手法，找到针感，将可吸收性外科缝线埋植在穴位的肌层或皮下组织内，拔针止血。若采用医用缝合针埋线法，一手用持针器夹住穿有可吸收性外科线的皮肤缝合针，另一手捏起两局部麻醉点之间的皮肤，将针从一侧局部麻醉点刺入，穿过肌层或皮下组织。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '颞颌关节脱位手法整复术', 1, 0.0, '患者坐位，助手固定患者头部。术者戴眼镜，口罩和手套。将纱布垫缠在拇指上以免被患者咬伤。术者将拇指放置复位侧的磨牙后区，其余手指托住下颌骨。拇指轻轻压下颌骨向下。逐渐增加力量按压3-5分钟，直到感觉髁突移动，然后将下颌向后轻轻推动，直到感觉髁突滑入关节窝。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '骨折超关节夹板外固定术', 1, 0.0, '将棉垫、纸压垫和夹板压垫准确地放在骨折适当位置上，要求夹板长度超出临近关节，并用纱布固定，以免滑动。捆绑束带时用力要均匀，其松紧度应使束带在夹板上可以不费力地上下推移1cm为宜。适用于关节面完整的关节内骨折或接近关节的干骺端骨折，如肱骨外髁颈、肱骨髁上、桡骨下端骨折、粗隆间、股骨髁上、胫骨上端及踝部骨折等。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '走罐治疗', 1, 0.0, '在施治部位皮肤上涂抹润滑剂，或用温水或药液，根据病情确定适宜的负压，选用经过消毒的罐具，先将罐吸拔在皮肤上，再按确定的路线、方向、面积，用适宜的力度，在皮肤上来回滑动，直至皮肤紫红为度，推罐时用力要均匀。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '锁骨骨折锁骨带外固定术', 1, 0.0, '锁骨骨折患者端坐凳子上，双手叉腰，嘱患者挺胸维持此位置行锁骨带外固定。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '水罐治疗', 1, 0.0, '根据病情，确定拔罐的部位和竹罐的规格及数量，将竹罐放入水中或药液中煮沸2-3分钟，然后用镊子将罐倒置夹起，迅速用干毛巾捂住罐口片刻，吸去罐内的水液，趁热将罐迅速吸拔于应拔部位，令其吸牢，并留置一定时间，数个竹罐依次操作。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '腰肌劳损推拿治疗', 1, 0.0, '患者俯卧位，术者用㨰法、按揉法、点压法、弹拨法在腰部两侧膀胱经、痛点及肌痉挛处操作，配合腰部后伸被动运动，提高痛阈、松解粘连，辨证选用传统腰部斜扳法、腰椎分步斜扳法，调整关节紊乱，用㨰法、揉法、叩打法在腰臀及大腿后外侧操作，结束整理。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '手针治疗', 1, 0.0, '按照手针疗法的取穴原则和方法，在手部选择特定穴区，选取适合规格的毫针，采用平刺、斜刺或直刺法实施操作。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '模拟针刺手法电针治疗', 1, 0.0, '通过电脉冲刺激装置，模拟针灸临床常用的多种针刺手法作用于机体，在体现一般电针疗法作用的同时，又有手法运针效果，根据病情选用不同的模拟针刺手法、控制刺激量、确定治疗时间。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '穴位注射治疗', 1, 0.0, '根据病情，确定穴位，选择药物及浓度、注射器和注射针型号，确定准确的进针位置，皮肤常规消毒后进行注射，针头刺入穴位得气后，回抽针芯，无回血、无回液即注入一定量的药物，在注射过程中要密切观察患者的反应。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '电冷针灸治疗', 1, 0.0, '毫针常规针刺，得气后，接通电子冷热针灸仪器，根据疾病性质、病变部位、病势急缓等选择温度、时间，通电后使针体制冷，通电10-15分钟。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '头针治疗', 1, 0.0, '按照头针疗法的取穴原则，选择头部特定的穴区，将毫针快速刺入帽状腱膜下层，采用快速捻转针法或提插手法实施操作，出针后要压迫止血。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '温针灸治疗', 1, 0.0, '在普通针刺基础上，再于针柄上放置艾绒或艾条段，点燃，可根据病情，更换数次艾绒或艾条段。密切注意灸处感觉，防止烫伤。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '大关节粘连手法松解术', 1, 0.0, '在两助手的牵引固定患肢下，术者结合多种松筋、弹拨、关节摇转等，行粘连处松解，松解过程中可听到松解声，反复数次，手法松解后需配合患者功能锻炼。大关节包括肩关节、肘关节、腕关节、髋关节、膝关节、踝关节。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '穴位放血治疗', 1, 0.0, '根据病情确定穴位，选择放血针具，持针具快速刺入到合适的深度，快速出针，挤出适量的血液，放血结束后压迫止血，一般术前应对腧穴充分按揉，使之充血后再行针刺。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '退行性膝关节炎推拿治疗', 1, 0.0, '患者取坐位或卧位，术者用㨰法、点穴法、按揉法、弹拨法等在髌韧带、膝髌周围、内外侧副韧带等操作，重点刺激痛点及阳陵泉、血海等穴位，舒筋松解，施用屈膝摇法，同时配合膝关节的屈伸、旋转等被动活动，松解关节粘连，滑利关节，最后用擦法结束整理。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '舌针治疗', 1, 0.0, '按照舌针疗法的取穴原则和方法，在舌体上的特定穴区，选取适合规格的毫针进行针刺或采用放血针具放血的方法实施操作。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '温灸器灸法', 1, 0.0, '根据病情，选定腧穴，确定所需艾段和灸量，将艾段放置于温灸器中，安置体位，密切观察灸处肤色变化和患者神情反应，以了解灸处感觉和病情变化，及时调整灸量，防止烫伤。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '关节粘连手法松解术', 1, 0.0, '在两助手牵引固定患肢下，术者采用理筋、松筋、弹拨，行粘连处松解，手法松解后需配合患者功能锻炼。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '针刺运动治疗', 1, 0.0, '在运用普通针刺手法时，根据不同疾病，一边进行针刺，一边让患者配合做特别的动作或由医生帮助患者做特别的动作，以获得最佳疗效。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '骨折夹板局部外固定术', 1, 0.0, '将棉垫、纸压垫和夹板压垫准确地放在骨折适当位置上，并用纱布固定，以免滑动。捆绑束带时用力要均匀，其松紧度应使束带在夹板上可以不费力地上下推移1厘米为宜。适用于一般骨干骨折，如肱骨、桡尺骨、胫腓骨。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '鼻腔针刺治疗', 1, 0.0, '在鼻镜窥视下将毫针分别刺入鼻腔的鼻甲、鼻丘特定穴区，取针时用消毒棉球填入鼻孔内止血，30分钟后取出。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '面针治疗', 1, 0.0, '按照面针疗法的取穴原则和方法，在面部选择特定穴区，选取适合规格的毫针，采用平刺、斜刺或直刺针法实施操作。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '项针治疗', 1, 0.0, '按照项针疗法的取穴原则和方法，在颈项部选择特定穴区，选取适合规格的毫针，采用直刺或斜刺法实施操作，要严格掌握针刺的角度、深度。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '肱骨外上髁炎推拿治疗', 1, 0.0, '患者坐位或仰卧位，术者用㨰法、按揉法、点压法等在患肢内外侧面操作，重点刺激肱骨外上髁处及曲池、手三里等穴位，舒筋松解，施用拔伸法牵引肘关节，拇指弹拨肱骨外上髁处，配合肘关节的屈伸、前臂的旋转活动，松解局部粘连，恢复肘关节的活动功能，最后用拿法、大鱼际擦法等结束整理。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '第三腰椎横突综合征推拿治疗', 1, 0.0, '患者俯卧位，术者用㨰法、按揉法重点在第三腰椎横突处操作，缓解肌紧张，作与第三腰椎横突处条索状硬结垂直方向的弹拨，配合腰部后伸等被动活动，消散瘀结，松解局部粘连。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '夹脊针治疗', 1, 0.0, '按照夹脊针疗法的取穴原则和方法，在脊柱两侧选择与疾病相应节段的夹脊穴或刺激点，选取适合规格的毫针，采用直刺法、斜刺法实施操作，要严格掌握针刺的角度、深度。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '直接灸治疗', 1, 0.0, '指根据病情、患者体质和穴位等确定选用化脓灸或非化脓灸、补泻方法、灸量，安置体位、审定穴位所在，施术时密切观察灸处肤色变化和患者神情变化、灸处感觉、病情变化，根据病情调整灸量。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '腹针治疗', 1, 0.0, '按照腹针疗法的取穴原则与方法，在腹部选择特定穴区，选取适合规格的毫针，采用直刺法实施操作。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '皮内针治疗', 1, 0.0, '选择适宜的不同规格、不同形状的一次性皮内针，皮肤常规消毒后进针，用颗粒型皮内针治疗时，一手将腧穴部皮肤向两侧舒张，另一手持镊子夹持针尾平刺入腧穴皮内，用揿钉型皮内针治疗时，一手固定腧穴部皮肤，另一手持镊子夹持针尾直刺入腧穴皮内，其后用胶布粘贴固定，嘱患者每日自行按压3-4次，一般1-3天后出针。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '艾条灸治疗', 1, 0.0, '根据病情、患者体质和穴位功能等确定选用温和灸、雀啄灸或回旋灸，确定补泻方法及灸量，安置体位，审定穴位所在。施术时密切观察灸处肤色变化和患者神情变化，注意灸处感觉和病情变化，调整艾条和灸处皮肤距离及灸量，防止烫伤。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '天灸治疗', 1, 0.0, '选用某些有刺激性的中药，并对药物进行中药饮片调配临方复杂炮制，贴敷在穴位上，使其局部自然发泡，通过刺激穴位达到治疗疾病的目的。根据病情、患者体质和穴位等确定和制备天灸药物，安置体位，施术中密切观察灸处肤色变化和患者感觉，及时调整灸疗时间，进行必要的发泡部位处理。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '电火针治疗', 1, 0.0, '使用电火针仪器治疗。仪器通电，使金属针头快速加热，针头被烧红，调节温度，通电加热在针体刺入穴位之前完成，快速刺入，快速出针，在体内不留针或短暂留针。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '体外物理振动排石治疗', 3, 1.0, '超声定位，调整患者体位，根据结石走向，顺走向轻推主振子，完成主动排石过程。不含影像学引导。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('', '', '睑板腺热脉动治疗', 3, 1.0, '含向患者说明注意事项，调整治疗参数，患者坐于治疗椅上，对治疗眼进行表面麻醉，眼周皮肤消毒。扒开上睑，轻柔地将治疗头先放入上睑，再扒开下睑，轻柔地将治疗头放入下睑，护士协助用胶布固定治疗头于额部等。进行睑板腺热脉动治疗期间，观察患者和仪器参数情况。完成后，缓慢地取出治疗头，先下后上。再擦拭眼部分泌物，清洁消毒，观察患者治疗情况。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('化验费', 'A008', '胃蛋白酶原Ⅰ(PGⅠ)测定', 3, 1.0, '指酶免法。包括胃蛋白酶原Ⅱ测定', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('化验费', 'A008', '抗磷脂酶A2受体(PLA2R)抗体检测', 3, 1.0, '样本类型：血液。样本采集、签收、处理，加免疫试剂，温育，检测，质控，审核结果，录入实验室信息系统或人工登记，发送报告；按规定处理废弃物；接受临床相关咨询。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('检查费', 'A007', 'ALK蛋白伴随诊断', 3, 1.0, '石蜡包埋组织于切片机切片，由全自动免疫组化染色仪进行脱蜡、水化，抗原修复，一抗反应(多克隆或单克隆)，酶标记二抗，亲合物或多聚物反应，显色，后由病理医师在显微镜下结合系统对照与对比阴性质控对照片根据判读指南综合进行诊断，并出具病理报告。新鲜冷冻组织，细胞涂片，组织印片参照相应方法制片，按规定处理废弃物。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('材料费', 'A012', '三叉神经半月节球囊压迫术', 3, 1.0, '用于治疗三叉神经痛。术前全面评估病人，全麻下采取Hartel穿刺技术，选择患侧口角外侧2-3cm处为穿刺点，引导穿刺针至卵圆孔，拔出针芯，将球囊导入Meckel腔，抽出导丝向球囊注入含碘非离子造影剂充盈球囊，并观察球囊形状，直至出现理想的倒梨形或哑铃型，持续压迫三叉神经半月节1-3min(术后再次复发者持续压迫3-5min)，压迫结束后，撤出导管及穿刺针，压迫穿刺点止血几分钟。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '强脉冲光干眼治疗', 3, 1.0, '清洁面部，调整体位和治疗参数，戴防护眼镜，进行强脉冲光照射。治疗结束，清理患者面部。', '', '', '', 1, 2);
