INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('检查费', 'A007', '脑电超慢涨落分析', 1, 0.0, '由技师操作，通过头皮电极，记录脑电活动，继而由精神科医师分析脑内神经递质的变化。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '显微镜下脑干血肿清除术', 1, 0.0, '上头架，消毒铺巾，切皮，双极电刀止血，气钻或电钻颅骨钻孔，铣刀取下骨瓣，切开硬脑膜，显微镜下清除血肿，因位于脑的高级中枢，因此手术操作要求较高，避免脑干的再次损伤，还需止血。必要时放置引流装置，缝合硬脑膜，骨瓣复位，缝合，包扎。不含颅骨成形术、神经导航、神经电生理监测。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '显微镜下幕上深部脑室内肿物切除术', 1, 0.0, '指胶质瘤、脑膜瘤、先天性肿瘤、血管性肿瘤及其它肿物。上头架，消毒铺巾，切皮，双极止血，气钻或电钻颅骨钻孔，铣刀取下骨瓣，头架附加，切开硬脑膜，显微镜下切除肿物，必要时行超声吸引，因肿瘤位于深部，操作难度大，术中还需保护脑室内重要血管，避免损伤，切除后还需止血。必要时放置引流装置，缝合硬脑膜，骨瓣复位，缝合，包扎。不含神经导航、超声吸引、术中超声监测、神经电生理监测。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '颅骨缺损修补成形术', 1, 0.0, '全身麻醉，消毒铺巾，切皮，双极止血，分离皮瓣，显露整个颅骨缺损范围，置入颅骨修补材料，止血。必要时放置引流装置，缝合，包扎。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '显微镜下幕上深部脑脓肿清除术', 1, 0.0, '上头架，消毒铺巾，切皮，双极止血，气钻或电钻颅骨钻孔，铣刀取下骨瓣，切开硬脑膜，显微镜下切除脑脓肿。必要时放置引流装置，缝合硬脑膜，骨瓣复位，缝合，包扎。不含神经导航。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('材料费', 'A012', '显微镜下颅内巨大动脉瘤夹闭切除术', 1, 0.0, '指对直径大于2.5厘米巨大瘤指动脉瘤进行夹闭。上头架，消毒铺巾，切皮，双极止血，气钻或电钻颅骨钻孔，铣刀取下骨瓣，头架附加，切开硬脑膜，显微镜下分离，夹闭并切除动脉瘤，颅内巨大动脉瘤手术风险很大、因解剖关系、可操作空间狭小、分离及处理动脉瘤有较大困难，术中一旦动脉瘤破裂可造成致命性的大出血。动脉瘤夹闭后还需再止血，缝合硬脑膜，骨瓣复位，缝合，包扎。不含神经导航、荧光造影、神经电生理监测、术中超声监', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '显微镜下幕上深部肿物切除术', 1, 0.0, '指该部位常见肿瘤及其他肿物切除。上头架，消毒铺巾，切皮，双极止血，气钻或电钻颅骨钻孔，铣刀取下骨瓣，头架附加，切开硬脑膜，显微镜下切除肿物，止血。必要时放置引流装置，缝合硬脑膜，骨瓣复位，缝合，包扎。不含内镜、术中超声监测、神经电生理监测。不含神经导航、超声吸引、术中超声监测、神经电生理监测。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '显微镜下第三脑室后肿物切除术', 1, 0.0, '指胶质瘤、脑膜瘤、血管瘤、生殖细胞肿瘤、表皮样囊肿、松果体细胞瘤、转移瘤及其它肿物等。上头架，消毒铺巾，切皮，双极止血，气钻或电钻颅骨钻孔，铣刀取下骨瓣，头架附加，切开硬脑膜，显微镜下切除肿物，根据情况行超声吸引，该处肿瘤位置较深，靠近脑干及颅神经，手术中肿瘤分离困难。手术时间长，术后感染发生率高。肿瘤切除后需严格止血。必要时放置引流装置，缝合硬脑膜，骨瓣复位，缝合，包扎。不含神经导航、神经电生理', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '显微镜下第三脑室肿物切除术', 1, 0.0, '指胶质瘤、脑膜瘤、血管瘤、垂体瘤、错构瘤、颅咽管瘤、生殖细胞肿瘤、表皮样囊肿、松果体细胞瘤、转移瘤等。上头架，消毒铺巾，切皮，双极止血，气钻或电钻颅骨钻孔、铣刀取下骨瓣，头架附加，切开硬脑膜，显微镜下切除肿物，必要时行超声吸引，该处肿瘤位于深部脑室，靠近脑干及颅神经，手术中肿瘤分离困难。手术时间长，术后脑积水，颅内感染发生率高，可能需要术后二次分流手术。肿瘤切除后需严格止血，缝合硬脑膜，骨瓣复位，', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '显微镜下第三脑室前肿物切除术', 1, 0.0, '指胶质瘤、脑膜瘤、血管瘤、垂体瘤、错构瘤、颅咽管瘤、生殖细胞肿瘤、表皮样囊肿、松果体细胞瘤、转移瘤等。上头架，消毒铺巾，切皮，双极止血，气钻或电钻颅骨钻孔、铣刀取下骨瓣，头架附加，切开硬脑膜，显微镜下切除肿物，必要时行超声吸引，该处肿瘤位于深部脑室，靠近脑干及颅神经，手术中肿瘤分离困难。手术时间长，术后脑积水，颅内感染发生率高，可能需要术后二次分流手术。肿瘤切除后需严格止血，缝合硬脑膜，骨瓣复位，', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '显微镜下脑干肿物切除术', 1, 0.0, '指脑干及周围的胶质瘤、脑膜瘤、血管瘤及其它肿物等。上头架，消毒铺巾，切皮，双极电刀止血，气钻或电钻颅骨钻孔，铣刀取下骨瓣，头架附加，切开硬脑膜，显微镜下切除肿物，必要时行超声吸引，还需止血，缝合硬脑膜，骨瓣复位，缝合，包扎。不含颅骨成形术、神经导航、神经电生理监测、术中超声监测、超声吸引。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '显微镜下颅内蛛网膜囊肿摘除术', 1, 0.0, '上头架，消毒铺巾，切皮，双极止血，气钻或电钻颅骨钻孔，铣刀取下骨瓣，头架附加，切开硬脑膜，显微镜及颅内镜下切除囊肿，止血，该病最大的危险是术后颅内出血，因此术中止血需耐心细致。必要时放置引流装置，缝合硬脑膜，骨瓣复位，缝合，包扎。不含神经导航、神经电生理监测。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '显微镜下丘脑肿物切除术', 1, 0.0, '指胶质瘤、淋巴瘤、转移瘤、错构瘤、生殖细胞肿瘤、血管性肿瘤等。上头架，消毒铺巾，切皮，双极止血，气钻或电钻颅骨钻孔，铣刀取下骨瓣，头架附加，切开硬脑膜，显微镜下切除肿物，必要时行超声吸引。丘脑解剖及功能复杂，手术要求精确，手术时间长，术后并发症多。肿瘤切除后止血困难，必要时放置引流装置，缝合硬脑膜，骨瓣复位，缝合，包扎。不含神经导航、神经电生理监测、术中超声监测、超声吸引。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '显微镜下幕上开颅探查术', 1, 0.0, '上头架，消毒铺巾，幕上开颅，切皮，双极止血，气钻或电钻颅骨钻孔，铣刀取下骨瓣，头架附加，切开硬脑膜，显微镜下肿物探查，止血，必要时放置引流装置，缝合硬脑膜，骨瓣复位，缝合，包扎。不含神经电生理监测。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '显微镜下幕上深部异物清除术', 1, 0.0, '上头架，消毒铺巾，切皮，双极止血，气钻或电钻颅骨钻孔，铣刀取下骨瓣，头架附加，切开硬脑膜，显微镜下取出异物，止血。必要时放置引流装置，缝合硬脑膜，骨瓣复位，缝合，包扎。不含神经导航。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '显微镜下鼓膜修补术', 1, 0.0, '局麻或全麻，耳显微镜监视下耳道消毒，鼓膜麻醉，用消毒好之移植材料修补鼓膜穿孔。不含移植材料切取。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '未完全萌出阻生牙拔除术', 1, 0.0, '指被牙龈覆盖的各类垂直阻生牙齿的拔除。麻醉，使用外科专用切割钻切开黏骨膜，翻瓣，去骨解除阻力，牙齿拔除，止血，伤口处理及缝合。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '萌出阻生牙拔除术', 1, 0.0, '指已萌出黏膜的近中、远中、颊舌向阻生牙。麻醉，解除阻力，分根，拔除，止血及伤口处理。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '骨性埋藏阻生牙拔除术', 1, 0.0, '指完全埋藏颌骨内的各类阻生牙及多生牙的拔除。局麻下切开牙龈，翻开黏骨膜瓣，使用专用切割钻去除埋藏牙表面的骨质，解除阻力，去冠分根，拔除阻生牙，清理牙槽窝，止血，伤口缝合。不含拔牙窝内植骨术。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '造口旁疝原位修补术', 1, 0.0, '全身麻醉，消毒铺巾，造口旁切口逐层进腹，探查，寻找疝囊，疝囊及瘢痕切除，探查腹腔，止血，原位缝合或各种方法补片修补，造口重建。能量设备止血，清点器具、纱布无误，冲洗伤口，放置引流，逐层缝合。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '腹壁疝修补术', 1, 0.0, '指白线疝、腰疝等的修补手术。全身麻醉，消毒铺巾，腹壁疝切口，逐层切开，寻找疝囊，切除多余疝囊，必要时进腹探查，按层次游离，必要时离断腹横肌止点，行肌后间隙分离腹壁成形，修整薄弱组织，疝环修补以及各种方法的无张力充填或补片修补，能量设备止血，清点器具、纱布无误，冲洗伤口，放置引流，逐层缝合。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '胰头部分切除胰肠吻合术', 1, 0.0, '指治疗慢性胰腺炎等胰空肠侧侧吻合术(Frey手术)。全身麻醉，消毒铺巾，逐层进腹，胰腺探查，胰头部分切除(coreout)，胰管切开，胰腺空肠吻合重建，空肠Roux-en-Y吻合，能量设备止血、腹腔引流管经腹壁另戳孔引出固定，清点器具、纱布无误，冲洗腹腔，逐层关腹。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '经腹会阴疝修补术', 1, 0.0, '逐层进腹，探查，寻找疝囊，切除疝囊，缝合或补片修补会阴薄弱区域，止血，清点器具、纱布无误，冲洗伤口，逐层关腹。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '保留十二指肠胰头切除术', 1, 0.0, '全身麻醉，消毒铺巾，逐层进腹，胰腺探查，解剖分离胰头部分切除，胰腺空肠吻合重建，空肠Roux-en-Y吻合，各种引流管、造瘘管经腹壁另戳孔引出固定，清点器具、纱布无误，冲洗腹腔，逐层关腹。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '胰腺癌联合脏器切除术', 1, 0.0, '全身麻醉，消毒铺巾，逐层进腹，经胰腺探查后评估可以切除，半胃、十二指肠、胆囊、中下段胆管、胰头、空肠近端切除，将邻近的受侵器官(结肠、小肠、泌尿生殖系器官)部分切除，相应区域淋巴结清扫、后腹膜区域淋巴结清扫，胆肠、胰肠、肠肠、泌尿道、生殖道重建，空肠造瘘，泌尿道造瘘，能量设备止血，将各种腹腔引流管，造瘘管分别经腹壁另戳孔引出固定，清点器具，纱布无误，冲洗腹腔，逐层关腹。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '无张力腹股沟疝修补术', 1, 0.0, '麻醉，消毒铺巾，腹股沟或股疝切口，逐层切开，探查，解剖腹股沟管或股管，寻找疝囊，游离疝囊，充填式内环修补，以及各种方法的无张力或补片修补，能量设备止血，清点器具、纱布无误，冲洗伤口，必要时放置引流，逐层缝合。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '胰管空肠侧侧吻合术', 1, 0.0, '指慢性胰腺炎胰管狭窄的内引流手术。全身麻醉，消毒铺巾，逐层进腹，胰腺探查，切开胰腺，显露近端胰管扩张部，与空肠侧侧吻合，能量设备止血，腹腔引流管经腹壁另戳孔引出固定，清点器具，纱布无误，冲洗腹腔，逐层关腹。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '经腹筋膜内子宫切除术', 1, 0.0, '指恶性子宫肿瘤，手术难度大于单纯全子宫切除术，子宫各韧带切除范围为宫旁0.5-1厘米。消毒铺巾、开腹，全面探查后切除双侧卵巢固有韧带、双侧输卵管峡部、双侧子宫圆韧带，打开阔韧带前后页，下推膀胱，下推直肠，切断双侧子宫动静脉，切断双侧子宫主韧带和骶韧带，夹切缝合阴道旁组织，切除部分阴道(以上各韧带、血管及阴道均距宫体0.5-1厘米处切断)，酌情留取腹腔引流管，缝合阴道断端，关腹。不含淋巴结清扫。', '', '', '', 1, 2);
INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '经腹全子宫切除术', 1, 0.0, '消毒铺巾，开腹，切除并缝合双侧卵巢固有韧带、双侧输卵管峡部、子宫圆韧带，打开阔韧带前后页，下推膀胱，下推直肠，切断双侧子宫动静脉，切断双侧子宫主韧带和骶韧带，缝合阴道断端，止血，关腹。不含淋巴结清扫。', '', '', '', 1, 2);
