INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('治疗费', 'A005', '动力取向心理治疗', 3, 1.0, '动力取向心理治疗除重症抑郁症和精神病急性发病期外，适合从边缘状态到神经症水平的病人、家庭及团体治疗。医生针对患者的人格结构、依恋创伤等方面，制定治疗计划，运用动力取向的心理治疗技术，评估患者的人格结构和内心冲突，使病人对自己内在世界有更加深刻的领悟，从而发生改变达到治疗目的。该技术须由受过专业培训的治疗师开展完成治疗。', '', '', '', 1, 2);
