INSERT INTO `acic_ccs_uat`.`bas_medicine` (item_name, item_code, common_name, fee_level, common_ratio, include, exception, unit, remark, bas_deduction_rule_id, medicine_type) VALUES ('手术费', 'A013', '腹腔镜辅助阴式子宫肌瘤挖除术', 3, 1.0, '术前行宫颈阴道预处理，常规消毒铺巾，先腹腔镜探查腹腔，超声刀予以分离粘连。消毒阴道及宫颈，阴道拉钩暴露宫颈，注水形成水垫，打开宫颈直肠间隙或宫颈膀胱间隙，经阴道后穹隆或前穹隆进入腹腔，经腹膜切口，用两把单抓钳交替钳夹子宫体，并向外牵引，边牵引边将钳向宫底移动，同时助手将宫颈向内推送协助宫体外翻，暴露子宫后壁或前壁肌瘤部位。宫体注入垂体后叶素，用电刀切开肌瘤表面的子宫浆肌层，暴露瘤体。单抓钳钳夹瘤体', '', '', '', 1, 2);
